var msg3 = {}; 
var stat, outside, m3_mode, leaf;

var temp = msg.temp; // target temp
var mode = msg.mode;


var temp_den = flow.get("temp_den"); // avg den temp
var outside = global.get("outside");

if ( outside > temp ) {
    stat = "hotter out [ " + outside + "] [" + temp + " ]";
    node.status({fill:"red",shape:"ring",text: stat});
} else {
    stat = "colder out ["+ outside +"] ["+ temp +"]";
    node.status({ fill: "blue", shape: "ring", text: stat });
}

if (temp < 63 ) {
    leaf = true;
} else {
    leaf = false;
}

if (mode == "heat") {
    if (temp > temp_den) {
        m3_mode = "heating";        
    } else {
        m3_mode = "";
    }
} else {
    m3_mode = "off";
}

msg3.payload = {
    "ambient_temperature": temp_den,
    "target_temperature": temp,
    "hvac_state": m3_mode,
    "hvac_mode": msg.mode,
    "has_leaf": leaf,
    "away": false,
    "source": "brains"
};



return [msg3];

var stat;

if (msg.outside < 0) {
    msg.bias = 2;
    stat = "really frickin cold [2]";
    flow.set("bias", 2);
    node.status({ fill: "blue", shape: "ring", text: stat });

} else if (msg.outside < 37) {
    stat = "frickin cold [1]";
    node.status({ fill: "yellow", shape: "ring", text: stat });
    flow.set("bias", 1);    
    msg.bias = 1;

} else if (msg.outside < 50) {
    stat = "cold [0]";
    node.status({ fill: "green", shape: "ring", text: stat }); 
    flow.set("bias", 0);   
    msg.bias = 0;

} else {
    stat = "not bad [-1]";
    node.status({ fill: "red", shape: "ring", text: stat });    
    msg.bias = -1;
    flow.set("bias", -1);
}
    return msg;

var msg1 = {}, msg2 = {}, msg3 = {};
var m1, m2, m3, stat;
msg2.topic = "control";
msg3.topic = "control";


if (msg.payload === true) { // hold activated
    m1 = "START";           // start hour timer
    m2 = "close";           // close gate 1
    m3 = "open";            // open gate 2

    stat = "running";
    flow.set("den_timer", stat);
    node.status({fill:"green",shape:"ring",text: stat});

    msg1.payload = m1;
    msg2.payload = m2;
    msg3.payload = m3;
    return [msg1, msg2, msg3];
} else if(msg.payload === false) {
    flow.set("overide_den", false);
    m2 = "open";
    m3 = "close";

    stat = "stopped";
    flow.set("den_timer", stat);
    node.status({fill:"red",shape:"ring",text: stat});

    msg2.payload = m2;
    msg3.payload = m3;
    return [null, msg2, msg3];
} else {
    node.error(msg.payload);
    return [];
}

